    private void addMemberButtonActionPerformed(java.awt.event.ActionEvent evt) {
        // Get the data from the text fields
        String name = nameTextField.getText();
        String email = emailTextField.getText();
        String username = usernameTextField.getText();
        String password = new String(passwordField.getText());
        String phone = phoneTextField.getText();
        String address = addressTextField.getText();

        // Validate the input fields
        if (name.trim().isEmpty() || email.trim().isEmpty() || username.trim().isEmpty() || password.trim().isEmpty() || phone.trim().isEmpty() || address.trim().isEmpty()) {
            JOptionPane.showMessageDialog(this, "Vui lòng điền đầy đủ thông tin!", "Cảnh báo", JOptionPane.WARNING_MESSAGE);
            return;
        }

        // Check email format
        if (!email.matches("^[a-zA-Z0-9_+&*-]+(?:\\.[a-zA-Z0-9_+&*-]+)*@(?:[a-zA-Z0-9-]+\\.)+[a-zA-Z]{2,7}$")) {
            JOptionPane.showMessageDialog(this, "Email không hợp lệ!", "Lỗi", JOptionPane.ERROR_MESSAGE);
            return;
        }

        // Create MemberDAO instance to save the member information
        MemberDAO memberDAO = new MemberDAO();
        boolean isAdded = memberDAO.registerMember(name, email, username, password, phone, address);

        // Check if adding member is successful
        if (isAdded) {
            JOptionPane.showMessageDialog(this, "Thêm thành viên thành công!", "Thông báo", JOptionPane.INFORMATION_MESSAGE);
            AdminFrame adminFrame = new AdminFrame();
            adminFrame.setVisible(true);
            // Close the current window
            this.dispose();
        } else {
            JOptionPane.showMessageDialog(this, "Tên đăng nhập đã tồn tại hoặc thêm thất bại, vui lòng thử lại!", "Lỗi", JOptionPane.ERROR_MESSAGE);
        }
    }

